import React, {useState, initialStyle, useEffect} from 'react';

const LastPlayDetail = (data) => {
    const instance = data.props;
    console.log('last play');
    console.log(data.props);

    const Canvas = () => {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext("2d");
        const xCoordinate = 233;
        const yCoordinate = 65;
        // const xCoordinate = instance.locX;
        // const yCoordinate = instance.locY;

        ctx.beginPath();
        ctx.fillStyle = "orange";
        // ctx.fillRect(xCoordinate, yCoordinate, 15, 15);
        ctx.arc(100, 100, 50, 0, 2, Math.PI*2);
        ctx.stroke();
    }

    useEffect(()=>{
        Canvas();
    }, [])

    return (
        <div className="last-play">
            <div className="last-play-header">
                <p><img src={instance.playerImg} alt="play-image" /> Last Play @ { instance.clock } { instance.description }</p>

            </div>

            <div className="last-play-canvas">
                <canvas id="canvas" className="canvas"></canvas>
            </div>
        </div>
    )
}

export default LastPlayDetail;
