import React, {useState} from 'react';

const [data, setData] = useState([]);
const [error, setError] = useState(null);
const GetData = () => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g);
        console.log(data);
        setGameData(GetGameData(data));
        setLoaded(true);
    },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
    (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
    });
}

const GetGameData = (data) => {
    const instance = data;
    console.log(instance);
    const opponent = "";
    if(instance.vls.ta === 'HOU') {
        opponent = instance.vls.tn;
        const dataCached = true;
    }
    else{
        const opponent = (instance.hls.tn);
        const dataCached = true;
    }

    const GameData = {
        'date': instance.gdte,
        'time': instance.utctm,
        'city': instance.ac,
        'state': instance.as,
        'opponent': opponent,
        'status': instance.st
    };
    return GameData;
}
