import './App.css';
import React, { useState, useEffect} from 'react';
import LoadingScreen from './components/resources/LoadingScreen.jsx';

import GameDetail from './components/mainCards/GameDetail.jsx'; 
import GameStats from './components/mainCards/GameStats';
import PlayerStatsGameDetail from './components/mainCards/PlayerStatsGameDetail.jsx';
import TeamStatsDetail from './components/mainCards/TeamStatsDetail';
import Header from './components/mainCards/Header';

function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);
  const [ opponent, setOpponent ] = useState("");

  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g);
        console.log(data);
        if(data.hls.ta !== "HOU") {
          setOpponent(data.hls.ta);
        }
        else{
          setOpponent(data.vls.ta);
        }
        setLoaded(true);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );
  }, [])

  if (error) {
    return <div className="App">Error: {error.message}</div>
  } else if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else {
    return (
      <div className="App">

        <section className="header">
          <Header />
        </section>

        {/* Game Details */}
        <section className="game-data">
          <h4>Game Date: {data.gdtutc}</h4> 
          <h4>Game Start Time: {data.utctm}</h4>
          <h4>Location: {data.ac}, {data.as}</h4>
          <h4>Opponent: {opponent}</h4>
          <h4>Game Status: {data.p} Period</h4>
          <h4>Time: {data.cl}</h4>
        </section>


        {/* Last Play */}
        {/* <section className="last-play">
          <p>Last Play: </p>
          <p>{data.cl}</p>
          <p>{data.lpla.de}</p>
        </section> */}

        {/* <div className="table">
          <table>
            <thead>
            </thead>
            <tbody></tbody>
          </table>
        </div> */}
      </div>
    );
  }
}

export default App;
