import React, { useEffect, useState } from 'react';


const PointLeaders = (data) => {
    const instance = data.props;

    const [ first, setFirst ] = useState(null);
    const [ second, setSecond ] = useState(null);
    const [ third, setThird ] = useState(null);

    const home = instance.vls.pstsg;
    const away = instance.hls.pstsg;

    const getStars = () => {
        console.log('get starts');
        console.log(first);
        console.log(second);
        console.log(third);
        console.log(home);
        for(var x=0; x<home.length; x++) {
            console.log(home[x]);
            if(home[x].pts !== null) {
                if(home[x].pts === third.pts && home[x].num !== third.num) {
                    if(home[x].ast > third.ast) {
                        setThird(home[x]);
                    }
                }
                if(home[x].pts > third.pts && home[x].pts < second.pts) {
                    setThird(home[x]);
                }
                if(home[x].pts === second.pts) {
                    if(home[x].ast > second.ast) {
                        setThird(second);
                        setSecond(home[x]);
                    }
                }
                if(home[x].pts > second.pts && home[x].pts < first.pts) {
                    setThird(second);
                    setSecond(home[x]);
                } 
                if(home[x].pts === first.pts && home[x].num !== first.num) {
                    if(home[x].ast > first.ast) {
                        setThird(second);
                        setSecond(first);
                        setFirst(home[x]);
                    }
                }
                if(home[x].pts > first.pts) {
                    setThird(second);
                    setSecond(first);
                    setFirst(home[x]);
                }
            }

        }
        for(var z=0; z<away.length; z++) {
            console.log(away[z]);
            if(away[z].pts === third.pts && away[z].num !== third.num) {
                if(away[z].ast > third.ast) {
                    setThird(away[z]);
                }
            }
            if(away[z].pts > third.pts && away[z].pts < second.pts) {
                setThird(away[z]);
            }
            if(away[z].pts === second.pts) {
                if(away[z].ast > second.ast) {
                    setThird(second);
                    setSecond(away[z]);
                }
            }
            if(away[z].pts > second.pts && away[z].pts < first.pts) {
                setThird(second);
                setSecond(away[z]);
            } 
            if(away[z].pts > first.pts) {
                setThird(second);
                setSecond(first);
                setFirst(away[z]);
            }
        }
        console.log('updated');
        console.log(first);
        console.log(second);
        console.log(third);
        console.log(home);

    }


    useEffect(()=>{
        console.log(instance);
        setFirst(home[0]);
        setSecond(home[0]);
        setThird(home[0]);

        getStars();

    }, [])

    return (
        <div className="point-leaders">
            {/* <h1>{first.fn[0]}.{first.ln}</h1>
            <p>{first.pts}</p>
            <h2>{second.fn[0]}.{second.ln}</h2>
            <p>{second.pts}</p>
            <h3>{third.fn[0]}.{third.ln}</h3>
            <p>{third.pts}</p> */}
        </div>
    );
}

export default PointLeaders;
