import './App.css';
import React, { useState, useEffect} from 'react';
import LoadingScreen from './components/resources/LoadingScreen.jsx';

import GameDetail from './components/mainCards/GameDetail.jsx'; 
import GameStats from './components/mainCards/GameStats';
import PlayerStatsGameDetail from './components/mainCards/PlayerStatsGameDetail.jsx';
import TeamStatsDetail from './components/mainCards/TeamStatsDetail';


function openTab(evt, tabName) {
  // Declare all variables
  var i, tabcontent, tablinks;

  // Get all elements with class="tabcontent" and hide them
  tabcontent = document.getElementsByClassName("tabcontent");
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }

  // Get all elements with class="tablinks" and remove the class "active"
  tablinks = document.getElementsByClassName("tablinks");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }

  // Show the current tab, and add an "active" class to the button that opened the tab
  document.getElementById(tabName).style.display = "block";
  evt.currentTarget.className += " active";
}

function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);

  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res);
        console.log(data);
        console.log(data.g.htm);
        console.log(data.g.vls.ta);
        setLoaded(true);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>
  } else if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else {
    return (
      <div>
        <GameDetail props={data} />
        <div>
          <h4>Game Time: {data.g.htm}</h4> 
          <h4>Location: {data.g.ac}</h4>
          <h4>Opponent: {data.g.vls.ta}</h4>
        </div>

        <div>
          <h3>***** Last Play Block *****</h3>
        </div>

        <div className="table">
          <table>
            <thead>
            </thead>
            <tbody></tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default App;
