export default function GetPointLeaders(data) {

    let hls = data.hls.pstsg; 
    let vls = data.vls.pstsg;

    let hlsData = data.hls;
    let vlsData = data.vls;

    function FilterHls(hls) {
        let sortedHls = hls.pstsg.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let pstsg = sortedHls.reverse();

        let plyrUrl = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/';
        let hlsUrl = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/';

        let hlsDataSet = {
            'first': {
                'team': hls.ta,
                'name': pstsg[0].fn[0] + ', ' + pstsg[0].ln,
                'pts': pstsg[0].pts,
                'ast': pstsg[0].ast,
                'img': plyrUrl + hls.ta + '.png',
                'teamImg': hlsUrl + hls.ta + '.svg'
            },
            'second': {
                'team': hls.ta,
                'name': pstsg[1].fn[0] + ', ' + pstsg[1].ln,
                'pts': pstsg[1].pts,
                'ast': pstsg[1].ast,
                'img': plyrUrl + hls.ta + '.png',
                'teamImg': hlsUrl + hls.ta + '.svg'
            },
            'third': {
                'team': hls.ta,
                'name': pstsg[2].fn[0] + ', ' + pstsg[2].ln,
                'pts': pstsg[2].pts,
                'ast': pstsg[2].ast,
                'img': plyrUrl + hls.ta + '.png',
                'teamImg': hlsUrl + hls.ta + '.svg'
            }
        }
        return hlsDataSet;
    }

    function FilterVls(vls) {
        let sortedVls = vls.pstsg.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let pstsg = sortedVls.reverse();

        let plyrUrl = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/';
        let vlsUrl = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/';

        let vlsDataSet = {
            'first': {
                'team': vls.ta,
                'name': pstsg[0].fn[0] + ', ' + pstsg[0].ln,
                'pts': pstsg[0].pts,
                'ast': pstsg[0].ast,
                'img': plyrUrl + vls.ta + '.png',
                'teamImg': vlsUrl + vls.ta + '.svg'
            },
            'second': {
                'team': vls.ta,
                'name': pstsg[1].fn[0] + ', ' + pstsg[1].ln,
                'pts': pstsg[1].pts,
                'ast': pstsg[1].ast,
                'img': plyrUrl + vls.ta + '.png',
                'teamImg': vlsUrl + vls.ta + '.svg'
            },
            'third': {
                'team': vls.ta,
                'name': pstsg[2].fn[0] + ', ' + pstsg[2].ln,
                'pts': pstsg[2].pts,
                'ast': pstsg[2].ast,
                'img': plyrUrl + vls.ta + '.png',
                'teamImg': vlsUrl + vls.ta + '.svg'
            }
        }
        return vlsDataSet;
    }

    const filteredHls = FilterHls(hlsData);
    const filteredVls = FilterVls(vlsData);

    let query = [filteredHls,first, filteredHls.second, filteredHls.third, filteredVls.first, filteredVls.second, filteredVls.third];

    let sortedQuery = query.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    })

    let leaders = [sortedQuery[0], sortedQuery[1], sortedQuery[2]];

    let first = {
        'name': leaders[0].name,
        'pts': leaders[0].pts,
        'ast': leaders[0].ast,

    };

    let second = {
        'name': leaders[1].name,
        'pts': leaders[1].pts,
        'ast': leaders[1].ast,

    };

    let third = {
        'name': leaders[2].name,
        'pts': leaders[2].pts,
        'ast': leaders[2].ast,

    };

    let currentLeaders = {
        'first': first, 
        'second': second, 
        'third': third,
    }

    console.log('Current Leaders Loaded: ' + currentLeaders);
    return currentLeaders;
}
