import React, { useEffect, useState } from 'react';
import ReactSvgPieChart from "react-svg-piechart"
import LoadingScreen from '../resources/loadingScreen';

const BoxScoreGraph = (state) => {
    const instance = state.props;
    const [ loaded, setLoaded ] = useState(false);

    const [ dataSet, setaDataSet ] = useState([]);

    const [ fgpHls, setFgpHls ] = useState(null);
    const [ fgpVls, setFgpVls ] = useState(null);
    const [ tppHls, setTppHls ] = useState(null);
    const [ tppVls, setTppVls ] = useState(null);
    const [ ftpHls, setFtpHls ] = useState(null);
    const [ ftpVls, setFtpVls ] = useState(null);

    // FIELD GOALS
    const fgpV = Math.round((instance.vls.tstsg.fgm / instance.vls.tstsg.fga) * 100);
    const fgpH = Math.round((instance.hls.tstsg.fgm / instance.hls.tstsg.fga) * 100);

    // THREE POINTERS
    const tppH = Math.round((instance.hls.tstsg.tpm / instance.hls.tstsg.tpa) * 100);
    const tppV = Math.round((instance.vls.tstsg.tpm / instance.hls.tstsg.tpa) * 100);

    // FREE THROWS
    const ftpH = Math.round((instance.hls.tstsg.ftm / instance.hls.tstsg.fta) * 100);
    const ftpV = Math.round((instance.vls.tstsg.ftm / instance.vls.tstsg.fta) * 100);

    // OFFENSIVE REBOUNDS 
    const oRebOne = instance.hls.tstsg.oreb + instance.vls.tstsg.dreb;
    const orebH = Math.round((instance.hls.tstsg.oreb / oRebOne) * 100);

    const oRebTwo = instance.vls.tstsg.oreb + instance.hls.tstsg.dreb;
    const orebV = Math.round((instance.vls.tstsg.oreb / oRebTwo) * 100);

    // DEFENSIVE REBOUNDS 
    const drebOne = instance.hls.tstsg.oreb + instance.vls.tstsg.dreb;
    const drebH = Math.round((instance.hls.tstsg.oreb / drebOne) * 100);

    const drebTwo = instance.vls.tstsg.oreb + instance.hls.tstsg.dreb;
    const drebV = Math.round((instance.vls.tstsg.oreb / drebTwo) * 100);


    // TURNOVERS 

    // POINTS OFF TURNOVERS


    const fgpData = [
        {title: "Data 1", value: fgpHls, color: "#dd2b35"},
        {title: "Data 2", value: fgpVls, color: "#663694"},
    ]

    const tppData = [
        {title: "Data 1", value: tppHls, color: "#dd2b35"},
        {title: "Data 2", value: tppVls, color: "#663694"},
    ]

    const ftpData = [
        {title: "Data 1", value: ftpHls, color: "#dd2b35"},
        {title: "Data 2", value: ftpVls, color: "#663694"},
    ]

    const orebData = [
        {title: "Data 1", value: orebH, color: "#dd2b35"},
        {title: "Data 2", value: orebV, color: "#663694"},
    ]

    const drebData = [
        {title: "Data 1", value: drebH, color: "#dd2b35"},
        {title: "Data 2", value: drebV, color: "#663694"},
    ]

    useEffect(()=>{
        setFgpHls(fgpH);
        setFgpVls(fgpV);
        setTppHls(tppH);
        setTppVls(tppV);
        setFtpHls(ftpH);
        setFtpVls(ftpV);
        setOrebHls(orebH);
        setOrebVls(orebV);

        setLoaded(true);
        console.log(loaded);
    }, []);

    if ( loaded ) {
        return (
            <div className="graph">
                <h1>Feild Breakdown</h1>
                <ReactSvgPieChart
                    data={fgpData}
                    // If you need expand on hover (or touch) effect
                    expandOnHover
                    // If you need custom behavior when sector is hovered (or touched)
                    onSectorHover={(d, i, e) => {
                        if (d) {
                            console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                        } else {
                            console.log("Mouse leave - Index:", i, "Event:", e)
                        }
                    }}
                />
                <h1>Three Point Breakdown</h1>
                <ReactSvgPieChart
                    data={tppData}
                    // If you need expand on hover (or touch) effect
                    expandOnHover
                    // If you need custom behavior when sector is hovered (or touched)
                    onSectorHover={(d, i, e) => {
                        if (d) {
                            console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                        } else {
                            console.log("Mouse leave - Index:", i, "Event:", e)
                        }
                    }}
                />
                <h1>Free Throw Breakdown</h1>
                <ReactSvgPieChart
                    data={ftpData}
                    // If you need expand on hover (or touch) effect
                    expandOnHover
                    // If you need custom behavior when sector is hovered (or touched)
                    onSectorHover={(d, i, e) => {
                        if (d) {
                            console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                        } else {
                            console.log("Mouse leave - Index:", i, "Event:", e)
                        }
                    }}
                />
                <h1>Offensive Rebound Breakdown</h1>
                <ReactSvgPieChart
                    data={orebData}
                    // If you need expand on hover (or touch) effect
                    expandOnHover
                    // If you need custom behavior when sector is hovered (or touched)
                    onSectorHover={(d, i, e) => {
                        if (d) {
                            console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                        } else {
                            console.log("Mouse leave - Index:", i, "Event:", e)
                        }
                    }}
                />
                <h1>Defensive Rebound Breakdown</h1>
                <ReactSvgPieChart
                    data={drebData}
                    // If you need expand on hover (or touch) effect
                    expandOnHover
                    // If you need custom behavior when sector is hovered (or touched)
                    onSectorHover={(d, i, e) => {
                        if (d) {
                            console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                        } else {
                            console.log("Mouse leave - Index:", i, "Event:", e)
                        }
                    }}
                />
            </div>
        )
    } else {
        return (
            <LoadingScreen />
        )
    }



}

export default BoxScoreGraph;
