export default function GetPointLeaders(data) {

    let hls = data.hls.pstsg; 
    let vls = data.vls.pstsg;

    let sortedHls = hls.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    });

    let sortedVls = vls.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    });

    let newHls = sortedHls.reverse();
    let newVls = sortedVls.reverse();

    let query = [newHls[0], newHls[1], newHls[2], newVls[0], newVls[1], newVls[2]];

    let sortedQuery = query.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    })

    // let awayImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.vls.ta + '.svg';
    // let homeImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.hls.ta + '.svg';

    let url = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/'


    let leaders = [sortedQuery[0], sortedQuery[1], sortedQuery[2]];

    let firstImg = url + leaders[0].ta + '.png';
    let first = {
        'name': leaders[0].fn + ', ' + leaders[0].ln,
        'pts': leaders[0].pts,
        'ast': leaders[0].ast,
        'img': firstImg
    };

    let secondImg = url + leaders[1].ta + '.png';
    let second = {
        'name': leaders[1].fn[0] + ', ' + leaders[1].ln,
        'pts': leaders[1].pts,
        'ast': leaders[1].ast,
        'img': secondImg
    };

    let thirdImg = url + leaders[2].ta + '.png';
    let third = {
        'name': leaders[2].fn[0] + ', ' + leaders[2].ln,
        'pts': leaders[2].pts,
        'ast': leaders[2].ast,
        'img': thirdImg
    };

    let currentLeaders = {
        'first': first, 
        'second': second, 
        'third': third,
    }
    console.log('Current Leaders Loaded: ' + currentLeaders);
    return currentLeaders;
}
