import React, {useEffect, useState} from 'react';
import GetLastPlayData from '../../reducers/LastPlayAPI';

const LastPlayDetail = (data) => {
    const [lastPlay, setLastPlay] = useState([]);
    const [loaded, setLoaded] = useState(false);

    const Canvas = (instance) => {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext("2d");

        ctx.beginPath();
        ctx.fillStyle = "blue";
        ctx.translate(canvas.width/2,canvas.height/2);

        ctx.arc(instance.locX,instance.locY,5,0,Math.PI*2);
        ctx.closePath();
        ctx.fill();

        ctx.save();
    }

    useEffect(()=>{
        setLastPlay(GetLastPlayData(data.props));
        console.log(lastPlay)
        Canvas(lastPlay);
        setLoaded(true);
    }, [])

    return (
        <>
            {loaded === true && ( <div className="last-play">
                <div className="last-play-header">
                    <p><img src={lastPlay.playerImg} alt="last play" /> Last Play @ { lastPlay.clock } { lastPlay.description }</p>
                </div>

                <div className="last-play-canvas">
                    <canvas id="canvas" className="canvas"></canvas>
                </div>
            </div>)}
        </>
    )
}

export default LastPlayDetail;
