import rocketsLogo from '../media/images/rocketsLogo.png';
export default function GetLastPlayData(data) {


    const getImage = () => {
        if(data.lpla.pid.length > 1) {
            const playerImg = "https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/" + data.lpla.pid + ".png";
            return playerImg;
        }
        else{
            const playerImg = rocketsLogo;
            return playerImg;
        }
    }

    const lastPlayData = {
        'clock': data.lpla.cl,
        'teamID': data.lpla.tid,
        'playerID': data.lpla.pid,
        'playerImg': getImage(),
        'eventType': data.lpla.etype,
        'event': data.lpla.evt,
        'messageType': data.lpla.mtype,
        'description': data.lpla.de,
        'locX': data.lpla.locX,
        'locY': data.lpla.locY,
        'opid': data.lpla.opid,
        'hs': data.lpla.hs,
        'vs': data.lpla.vs,
    };

    return lastPlayData;
}
