// This will replace LeadersApi
// Purpose will be to obtain team logo will also doing everything the LeadersApi does. 
// Doing this here for the purpose of upgrading without disrupting a stable project. 
// Goal is to seemlessly replace 


export default function GetPointLeadersData(data) {

    let hls = data.hls; 
    let vls = data.vls;

    function filterHls(hlsData) {
        let team = hlsData.ta;
        let evalHls = hlsData.pstsg;
        let sortedHls = evalHls.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let newHls = sortedHls(evalHls);

        let data = [
            {
                name: newHls[0].fn[0] + '.' + newHls[0].ln,
                pts: newHls[0].pts,
                ast: newHls[0].ast,
                pid: newHls[0].pid,
                ta: team
            }, {
                name: newHls[1].fn[1] + '.' + newHls[1].ln,
                pts: newHls[1].pts,
                ast: newHls[1].ast,
                pid: newHls[1].pid,
                ta: team
            }, {
                name: newHls[2].fn[2] + '.' + newHls[2].ln,
                pts: newHls[2].pts,
                ast: newHls[2].ast,
                pid: newHls[2].pid,
                ta: team
            }

        ]

        return data;
    }

    function filterVls(vlsData) {
        let team = vlsData.ta;
        let evalVls = vlsData.pstsg;
        let sortedVls = evalVls.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });
        let newVls = sortedVls(evalVls).reverse();
        let data = [
            {
                name: newVls[0].fn[0] + '.' + newVls[0].ln,
                pts: newVls[0].pts,
                ast: newVls[0].ast,
                pid: newVls[0].pid,
                ta: team
            }, {
                name: newVls[1].fn[1] + '.' + newVls[1].ln,
                pts: newVls[1].pts,
                ast: newVls[1].ast,
                pid: newVls[1].pid,
                ta: team
            }, {
                name: newVls[2].fn[2] + '.' + newVls[2].ln,
                pts: newVls[2].pts,
                ast: newVls[2].ast,
                pid: newVls[2].pid,
                ta: team
            }
        ]
        return data;
    }

    const hlsLdrs = filterHls(hls);
    const vlsLdrs = filterVls(vls);
    const query = hlsLdrs + vlsLdrs;
    let sortedQuery = query.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    });

    let newQuery = sortedQuery.reverse();
    const leaders = newQuery.slice(0, 3);

    let first = {
        'name': leaders[0].fn[0] + '.' + leaders[0].ln,
        'pts': leaders[0].pts,
        'ast': leaders[0].ast,
        'pid': leaders[0].pid,
        'ta': leaders[0].ta
    };

    let second = {
        'name': leaders[1].fn[0] + '.' + leaders[1].ln,
        'pts': leaders[1].pts,
        'ast': leaders[1].ast,
        'pid': leaders[1].pid,
        'ta': leaders[0].ta
    };

    let third = {
        'name': leaders[2].fn[0] + '.' + leaders[2].ln,
        'pts': leaders[2].pts,
        'ast': leaders[2].ast,
        'pid': leaders[2].pid,
        'ta': leaders[0].ta
    };

    let currentLeaders = {
        'first': first, 
        'second': second, 
        'third': third,
    };

    console.log('Current Leaders Loaded: ' + currentLeaders);
    return currentLeaders;

}
