import React, {useState, initialStyle, useEffect} from 'react';

const LastPlayDetail = (data) => {
    const instance = data.props;
    console.log('last play');
    console.log(data.props);
    const x = instance.locX;
    const y = instance.locY;

    const Canvas = () => {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext("2d");

        const newX = x+1;
        const newY = y+1;

        console.log(x, y);

        ctx.beginPath();
        ctx.fillStyle = "green";
        ctx.fillRect(x, y, newX, newY);
    }

    useEffect(()=>{
        Canvas();
    }, [])

    return (
        <div className="last-play">
            <canvas id="canvas" className="canvas"></canvas>
        </div>
    )
}

export default LastPlayDetail;
