import React, { useEffect, useState } from "react";
import BoxScore from "./boxScoreDetail";
import BoxScoreGraph from "./boxScoreGraph";


const BoxScoreSwitch = (data) => {
    const instance = data.props; 
    const [ buttonName, setButtonName ] = useState('graph');
    const [ state, setState ] = useState('boxScore');
    const [ nextState, setNextState ] = useState('graph');

    const onClick = (nextState) => {
        if(buttonName === 'graph') {
            setButtonName(nextState);
            setNextState('boxScore');
            setState(nextState);
        } else {
            setButtonName(nextState);
            setNextState('graph');
            setState(nextState);
        }
    };

    return (
        <div className="boxScoreSwitch">
            { state === 'boxScore' && (
                <BoxScore props={instance} />
            )}

            { state === 'graph' && (
                <BoxScoreGraph props={instance} />
            )}

            <button onClick={nextState}>{ buttonName }</button>

        </div>
    );
}

export default BoxScoreSwitch;
