export default function GetLastPlayData(data) {

    const playerID = data.lpla.pid;

    const getPlayerInfo = () => {
        for(var x=0; x<data.pstsg.length; x++){
            if(data.pstsg[x].pid === playerID) {
                const playerInfo = {
                    'playerID': playerID,
                    'playerNum': data.pstsg[x].num,
                    'playerfName': data.pstsg[x].fn,
                    'playerlName': data.pstsg[x].ln,
                }
                return playerInfo;
            }
        }
    }

    const lastPlayData = {
        'clock': data.lpla.cl,
        'teamID': data.lpla.tid,
        'playerID': playerID,
        'eventType': data.lpla.etype,
        'event': data.lpla.evt,
        'messageType': data.messageType,
        'description': data.lpla.de,
        'playerInfo': getPlayerInfo()
    };

    return lastPlayData;
}
