import React, {useState} from 'react';

const getStatus = (data) => {
    const [status, setStatus] = useState('');

    if(data.st === 2) {
        setStatus(data.cl);
    }
    if(data.st === 1) {
        setStatus('Pregame');
    }
    if(data.st === 3) {
        setStatus('Final');
    }
    return status;
};

export default function GetGameData(data) {

    const status = getStatus(data); 

    const GameData = {
        'date': data.gdte,
        'time': data.utctm,
        'city': data.ac,
        'state': data.as,
        'home': data.hls.tn,
        'away': data.vls.tn,
        'status': status
    };

    return GameData;
}
