import './App.css';
import React, { useState, useEffect} from 'react';

import GetGameData from './reducers/GameDataAPI';
import GetScoreData from './reducers/ScoreAPI';

import LoadingScreen from './components/resources/LoadingScreen.jsx';

// import GameDetail from './components/mainCards/GameDetail.jsx'; 
// import GameStats from './components/mainCards/GameStats.jsx';
// import PlayerStatsGameDetail from './components/mainCards/PlayerStatsGameDetail.jsx';
// import TeamStatsDetail from './components/mainCards/TeamStatsDetail.jsx';
import Header from './components/resources/header.jsx';
import ScoreBoard from './components/mainCards/scoreBoard';

function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);
  const [ gameDetail, setGameDetail ] = useState([]);
  const [ scoreDetail, setScoreDetail ] = useState([]);


  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g);
        console.log(data.a);
        setGameDetail(GetGameData(data));
        setScoreDetail(GetScoreData(data));
        setLoaded(true);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );

  }, []);

  if (error) {
    return <div className="App">Error: {error.message}</div>
  } else if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else {
    return (
      <div className="App">
        {/* Banner Section */}
        <section className="header">
          <Header props={gameDetail} />
          <ScoreBoard props={scoreDetail} />
        </section>
        <section className="tab">

        </section>
        {/* Last Play */}
        {/* <section className="last-play">
          <p>Last Play: </p>
          <p>{data.cl}</p>
          <p>{data.lpla.de}</p>
        </section> */}

        {/* <div className="table">
          <table>
            <thead>
            </thead>
            <tbody></tbody>
          </table>
        </div> */}
      </div>
    );
  }
}

export default App;
