import React, { useEffect, useState } from "react";
import BoxScore from "./boxScoreDetail";
import BoxScoreGraph from "./boxScoreGraph";


const BoxScoreSwitch = (data) => {
    const instance = data.props; 
    const [ buttonName, setButtonName ] = useState('Box Score');
    const [ state, setState ] = useState('graph');
    const [ nextState, setNextState ] = useState('boxScore');

    const onClick = () => {
        if(buttonName === 'Graph') {
            setButtonName('Box Score');
            setState(nextState);
            setNextState('boxScore');
        } else {
            setButtonName('Graph');
            setState(nextState);
            setNextState('graph');
        }
    };

    return (
        <div className="bpx-score-switch">
            <div>
                { state === 'boxScore' && (
                    <BoxScore props={instance} />
                )}

                { state === 'graph' && (
                    <BoxScoreGraph props={instance} />
                )}
            </div>

            <button onClick={onClick}>{ buttonName }</button>

        </div>
    );
}

export default BoxScoreSwitch;
