export default function GetPointLeaders(data) {

    let hls = data.hls.pstsg; 
    let vls = data.vls.pstsg;

    let hlsData = data.hls;

    function FilterHls(hls) {
        let sortedHls = hls.pstsg.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let pstsg = sortedHls.reverse();
        let plyrUrl = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/';
        let hlsUrl = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/';

        let hlsDataSet = {
            'team': hls.ta,
            'name': pstsg[0].fn[0] + ', ' + pstsg[0].ln,
            'pts': pstsg[0].pts,
            'ast': pstsg[0].ast,
            'img': plyrUrl + hls.ta + '.png',
            'teamImg': hlsUrl + hls.ta + '.svg'

        }
    }

    FilterHls(hlsData);

    let sortedHls = hls.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    });

    let sortedVls = vls.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    });

    let newHls = sortedHls.reverse();
    let newVls = sortedVls.reverse();

    let query = [newHls[0], newHls[1], newHls[2], newVls[0], newVls[1], newVls[2]];

    let sortedQuery = query.sort((a, b) => {
        if(a.pts < b.pts) {
            return -1
        } else if(a.pts > b.pts) {
            return 1
        } else {
            return 0
        }
    })

    // let awayImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.vls.ta + '.svg';
    // let homeImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.hls.ta + '.svg';

    let url = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/'

    let leaders = [sortedQuery[0], sortedQuery[1], sortedQuery[2]];

    let first = {
        'name': leaders[0].fn + ', ' + leaders[0].ln,
        'pts': leaders[0].pts,
        'ast': leaders[0].ast,

    };

    let second = {
        'name': leaders[1].fn[0] + ', ' + leaders[1].ln,
        'pts': leaders[1].pts,
        'ast': leaders[1].ast,

    };

    let third = {
        'name': leaders[2].fn[0] + ', ' + leaders[2].ln,
        'pts': leaders[2].pts,
        'ast': leaders[2].ast,

    };

    let currentLeaders = {
        'first': first, 
        'second': second, 
        'third': third,
    }

    console.log('Current Leaders Loaded: ' + currentLeaders);
    return currentLeaders;
}
