import React, {useEffect, useState} from 'react';
import GetLastPlayData from '../../reducers/LastPlayAPI';

const LastPlayDetail = (data) => {
    const instance = data.props;
    const [lastPlay, setLastPlay] = useState(null);

    const Canvas = (instance) => {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext("2d");
        // const xCoordinate = 233;
        // const yCoordinate = 65;

        ctx.beginPath();
        ctx.fillStyle = "blue";
        ctx.translate(canvas.width/2,canvas.height/2);

        ctx.arc(instance.locX,instance.locY,5,0,Math.PI*2);
        ctx.closePath();
        ctx.fill();

        ctx.save();
    }

    useEffect(()=>{
        setLastPlay(GetLastPlayData(instance));
        Canvas(lastPlay);
    }, [])

    return (
        <div className="last-play">
            <div className="last-play-header">
                <p><img src={instance.playerImg} alt="last play" /> Last Play @ { instance.clock } { instance.description }</p>
            </div>

            <div className="last-play-canvas">
                <canvas id="canvas" className="canvas"></canvas>
            </div>
        </div>
    )
}

export default LastPlayDetail;
