import React, {useState} from 'react';


export default function GetGameData(data) {
    const getStatus = (data) => {

        if(data.st === 2) {
            return data.cl;
        }
        if(data.st === 1) {
            return 'Pregame';
        }
        if(data.st === 3) {
            return 'Final';
        }
    };

    const status = getStatus(data); 

    const GameData = {
        'date': data.gdte,
        'time': data.utctm,
        'city': data.ac,
        'state': data.as,
        'home': data.hls.tn,
        'away': data.vls.tn,
        'status': status
    };

    return GameData;
}
