import './App.css';
import React, { useState, useEffect} from 'react';

// Reducers
import GetGameData from './reducers/GameDataAPI';
import GetScoreData from './reducers/ScoreAPI';
import GetLastPlayData from './reducers/LastPlayAPI';

// Components
import LoadingScreen from './components/resources/loadingScreen.jsx';
import Header from './components/resources/header.jsx';
import Footer from './components/resources/footer.jsx';
import ScoreBoard from './components/mainCards/scoreBoard.jsx';
// import LastPlayDetail from './components/tabs/lastPlayDetail.jsx';
import TeamStats from './components/tabs/teamStats.jsx';


function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);
  const [ gameDetail, setGameDetail ] = useState([]);
  const [ scoreDetail, setScoreDetail ] = useState([]);

  const [ showTeamData, setShowTeamData ] = useState(false);
  const [ teamData, setTeamData ] = useState([]);

  const homeTeam = () => {
    setShowTeamData(true);
    setTeamData(data.hls.pstsg);
  }

  const awayTeam = () => {
    setShowTeamData(true);
    setTeamData(data.vls.pstsg);
  }

  const ghostBox = () => {
    setShowTeamData(false);
  }

  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g); 
        console.log('data');
        console.log(data);
        setGameDetail(GetGameData(data));
        setScoreDetail(GetScoreData(data));
        setTeamData(data.hls.pstsg);
        setLoaded(true);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );
  }, []);

  if (error) {
    return <div className="App">Error: {error.message}</div>
  } else if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else {
    return (
      <div className="App">
        {/* Banner Section */}
        <section className="header">
          <Header props={gameDetail} />
          <ScoreBoard props={scoreDetail} />
        </section>
        <section className="tab">
          {/* <Tabs /> */}
          <div>
            <div className="tab-bar">
              <button onClick={ghostBox}>Hide Team Stats</button>
              <button onClick={homeTeam}>{data.hls.ta}</button>
              <button onClick={awayTeam}>{data.vls.ta}</button>
            </div>
            <div className="tab-content">
              <TeamStats props={teamData} />
            </div>
        </div>
        </section>
      </div>
    );
  }
}

export default App;
