import React from 'react';

const LastPlayDetail = (data) => {
    const instance = data.props;
    console.log('last play');
    console.log(data.props);


    // for styling of elements
    const [style, setStyle] = useState(initialStyle); 

    const setCoordinates = (x,y) => {
    // You don't need whitespace in here, I added it for readability
    // I would recommend using something like EmotionJS for this
        return `position:absolute;   
                left:${x}px;         
                top:${y}px;`
    }


    return (
        <div className="last-play">
            <p>Last Play at {instance.clock} </p>
            <div 
                style = {style}
                onClick = { e => { 
                    const newStyle = 
                        setCoordinates(e.target.screenX,
                                        e.target.screenY);
                    setStyle(newStyle);
                }}> 
            </div>
        </div>
    )
}

export default LastPlayDetail;
