import React, { useEffect, useState } from 'react';
import LoadingScreen from '../resources/loadingScreen';


const PointLeaders = (data) => {

    const [ first, setFirst ] = useState(null);
    const [ second, setSecond ] = useState(null);
    const [ third, setThird ] = useState(null);

    const [ loaded, setLoaded ] = useState(false); 

    const getStars = (instance) => {
        const home = instance.vls.pstsg;
        const away = instance.hls.pstsg;

        let sortedHome = home.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        })

        let sortedAway = away.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        })

        let hls = sortedHome.reverse();
        let vls = sortedAway.reverse();

        console.log(hls);
        console.log(vls);

        const query = [hls[0], hls[1], hls[2], vls[0], vls[1], vls[2]];
        let sortedQuery = query.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        })

        const stars = [sortedQuery[0], sortedQuery[1], sortedQuery[2]];
        console.log(stars);

        setFirst(stars[0]);
        setSecond(stars[1]);
        setThird(stars[2]);
        setLoaded(true);
    }


    useEffect(()=>{
        getStars(data.props);
    }, [])

    if(!loaded) { 
        return (
            <LoadingScreen />
        )
    } else {
        return (
            <div className="point-leaders">
                <h1>Point Leaders</h1>
                <div className="point-leader-card">
                    <h1>{first.fn[0]}.{first.ln}</h1>
                    <p>{first.pts}</p>
                    <p>{first.ast}</p>
                </div>

                <div className="point-leader-card">
                    <h2>{second.fn[0]}.{second.ln}</h2>
                    <p>{second.pts}</p>
                    <p>{second.ast}</p>
                </div>

                <div className="point-leader-card">
                    <h3>{third.fn[0]}.{third.ln}</h3>
                    <p>{third.pts}</p>
                </div>
            </div>

        )

    };
}

export default PointLeaders;
