import React from 'react';

const ScoreBoard = (data) => {
    const instance = data.props;
    return (
        <div className="score-board">
            {/* Home Score */}
            <div className="home-score">
                <h4>{instance.homeTeam}</h4>
                <h1 className="sb-score">{instance.homeScore}</h1>
            </div>

            {/* Score Board */}
            <div className="col-sm-4 col-lg-4">
                <p>Time</p>
                <h3>{instance.time}</h3>
                <p>QTR {instance.period}</p>
            </div>

            {/* Away Score */}
            <div className="away-score">
                <h4>{instance.awayTeam}</h4>
                <h1 className="sb-score">{instance.awayScore}</h1>
            </div>

        </div>
    )
}

export default ScoreBoard;
