import './App.css';
import React, { useState, useEffect} from 'react';

// Reducers
import GetScoreData from './reducers/ScoreAPI';
// import GetLastPlayData from './reducers/LastPlayAPI';

// Components
import LoadingScreen from './components/resources/loadingScreen.jsx';
import Header from './components/resources/header.jsx';
import ScoreBoard from './components/mainCards/scoreBoard.jsx';
import WindowScoreBoard from './components/mainCards/windowScoreBoard.jsx';
// import LastPlayDetail from './components/tabs/lastPlayDetail.jsx';
// import TeamStats from './components/tabs/teamStats.jsx';
// import Tickets from './components/resources/tickets.jsx';

function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);

  const [ scoreDetail, setScoreDetail ] = useState([]);

  // const [ teamData, setTeamData ] = useState([]);
  // const [ lastPlayData, setLastPlayData ] = useState([]);

  // const [ showTeamData, setShowTeamData ] = useState(false);
  // const [ showLastPlay, setShowLastPlay ] = useState(true);
  // const [ showTickets, setShowTickets ] = useState(false);

  const [ isFullScreen, setIsFullScreen ] = useState(false);

  // const homeTeam = () => {
  //   setShowLastPlay(false);
  //   // setShowTeamData(true);
  //   // setTeamData(data.hls.pstsg);
  //   setShowTickets(false);
  // }

  // const awayTeam = () => {
  //   setShowLastPlay(false);
  //   setShowTeamData(true);
  //   // setTeamData(data.vls.pstsg);
  //   setShowTickets(false);
  // }

  // const lastPlay = () => {
  //   setShowTeamData(false);
  //   setShowLastPlay(true);
  //   setShowTickets(false);
  // }

  // const tickets = () => {
  //   setShowTeamData(false);
  //   setShowLastPlay(false);
  //   setShowTickets(true);
  // }

  const fullScreen = () => {
    setIsFullScreen(true);
  }

  const hideFullScreen = () => {
    setIsFullScreen(false);
  }

  const vlsImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.vls.ta + '.svg';
  const hlsImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.hls.ta + '.svg';


  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g); 
        console.log(data);
        setScoreDetail(GetScoreData(data));
        // setLastPlayData(GetLastPlayData(data));
        setLoaded(true);
        console.log('data loaded');
        console.log(loaded);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );
  }, []);

  if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else if (isFullScreen === true) {
    return (
      <div className="App">
        {/* Banner Section */}
        {/* <section className="header">
          <Header props={data} />
          <ScoreBoard props={scoreDetail} />
        </section>
        <section className="tab"> */}
          {/* <Tabs /> */}
          {/* <div className="tab-bar">
            <button onClick={homeTeam}>{data.hls.ta}</button>
            <button onClick={awayTeam}>{data.vls.ta}</button>
            <button onClick={lastPlay}>Last Play</button>
            <button onClick={tickets}>Tickets</button>
            <button onClick={hideFullScreen}>Minimize</button>
          </div>
          { showTeamData === true && (
            <div className="tab-content">
              <TeamStats props={teamData} />
            </div>
          )};
          { showLastPlay === true && (
            <LastPlayDetail props={lastPlayData}/>
          )}
          { showTickets === true && (
            <Tickets />
          )}
        </section> */}
      </div>
    );
  } else {
    return (
      <div className="App">
        <section className="window">
          <ScoreBoard props={scoreDetail} />
          <div className="window-logos">
            <img src={vlsImg} alt={data.vls.ta} />
            <h1>@</h1>
            <img src={hlsImg} alt={data.hls.ta} />
          </div>
          <div className="tab-content">
            <WindowScoreBoard props={data} />
          </div>
          <p className="window-lp-data">Last Play: {data.lpla.cl} {data.lpla.de}</p>
          <br></br>
          <button onClick={fullScreen}>FullScreen</button>
        </section>
      </div>
    )
  }
}

export default App;
