import React, { useEffect, useState } from 'react';


const PointLeaders = (data) => {
    const instance = data.props;

    const [ first, setFirst ] = useState(null);
    const [ second, setSecond ] = useState(null);
    const [ third, setThird ] = useState(null);

    const home = instance.vls.pstsg;
    const away = instance.hls.pstsg;

    const getStars = () => {
        console.log('get starts');
        console.log(first);
        console.log(second);
        console.log(third);
        console.log(home);
        for(var x=0; x<home.length; x++) {
            console.log(home[x]);
            if(home[x].pts === first.pts) {
                if(home[x].ast > first.ast){
                    setThird(second);
                    setSecond(first);
                    setFirst(home[x]);
                }
                if(home[x].ast < first.ast) {
                    setThird(second);
                    setSecond(home[x]);
                }
            } 
            if(first.pts < home[x].pts ) {
                setThird(second);
                setSecond(first);
                setFirst(home[x]);
            }
            if(second.pts === home[x].pts) {
                if(second.ast < home[x].ast) {
                    setThird(second);
                    setSecond(home[x]);
                } 
                if(second.ast > home[x].ast) {
                    setThird(home[x]);
                }
            }
            if(second.pts < home[x].pts && home[x].pts < first.pts) {
                setThird(second);
                setSecond(home[x]);
            }
            if(third.pts < home[x].pts && home[x].pts < second.pts) {
                setThird(home[x]);
            }
        }
        console.log(first);
        console.log(second);
        console.log(third);
        console.log(home);

        for(var y=0; y<away.length; y++) {
            console.log(away[y]);
            if(away[y].pts === first.pts) {
                if(away[y].ast > first.ast){
                    setThird(second);
                    setSecond(first);
                    setFirst(away[y]);
                }
                if(away[y].ast < first.ast) {
                    setThird(second);
                    setSecond(away[y]);
                }
            } 
            if(first.pts < away[y].pts ) {
                setThird(second);
                setSecond(first);
                setFirst(away[y]);
            }
            if(second.pts === away[y].pts) {
                if(second.ast < away[y].ast) {
                    setThird(second);
                    setSecond(away[y]);
                } 
                if(second.ast > away[y].ast) {
                    setThird(away[y]);
                }
            }
            if(second.pts < away[y].pts && away[y].pts < first.pts) {
                setThird(second);
                setSecond(away[y]);
            }
            if(third.pts < away[y].pts && away[y].pts < second.pts) {
                setThird(away[x]);
            }
        }

    }


    useEffect(()=>{
        console.log(instance);
        setFirst(home[0]);
        setSecond(home[0]);
        setThird(home[0]);

        getStars();

    }, [])

    return (
        <div className="point-leaders">
            <h1>{first.fn[0]}.{first.ln}</h1>
            <p>{first.pts}</p>
            <h2>{second.fn[0]}.{second.ln}</h2>
            <p>{second.pts}</p>
            <h3>{third.fn[0]}.{third.ln}</h3>
            <p>{third.pts}</p>
        </div>
    );
}

export default PointLeaders;
