export default function GetPointLeaders(data) {
    function pointLeaders(instance) {
        let hls = instance.hls.pstsg; 
        let vls = instance.vls.pstsg;

        let sortedHls = hls.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let sortedVls = vls.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        });

        let newHls = sortedHls.reverse();
        let newVls = sortedVls.reverse();

        let query = [hls[0], hls[1], hls[2], vls[0], vls[1], vls[2]];

        let sortedQuery = query.sort((a, b) => {
            if(a.pts < b.pts) {
                return -1
            } else if(a.pts > b.pts) {
                return 1
            } else {
                return 0
            }
        })

        let awayImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.vls.ta + '.svg';
        let homeImg = 'https://i.cdn.turner.com/nba/nba/assets/logos/teams/primary/web/' + data.hls.ta + '.svg';

        let url = 'https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/'


        let leaders = [sortedQuery[0], sortedQuery[1], sortedQuery[2]];
        console.log(leaders);

        let first = leaders[0];
        let firstImg = url + first.ta + '.png';
        let second = leaders[1];
        let secondImg = url + second.ta + '.png';
        let third = leaders[2];
        let thirdImg = url + third.ta + '.png';

        let currentLeaders = {
            first: first, 
            firstImg: firstImg, 
            second: second, 
            secondImg: secondImg,
            third: third,
            thirdImg: thirdImg
        }
        return currentLeaders;
    }

    return pointLeaders;

}
