import React, { useEffect, useState } from 'react';


const ListenWatchTab = (data) => {

    const [ gamesDone, setGamesDone ] = useState(false);

    useEffect(()=>{
        console.log('listenWatchTab');
        console.log(data);

        if(data.props === 1 || data.props === 2){
            setGamesDone(false);
        }
        if(data.props === 3){
            setGamesDone(true);
        }

    }, [])

    if(!gamesDone) {
        return (
            <div className="listen-watch">
                <div className="listen-watch-tab">
                    {/* This needs to be routing to a prefered or sponsored video broadcast  */}
                    <a href="https://www.nba.com/rockets/broadcast-information" target="blank"><i class="fa fa-tv"></i></a>
                    {/* This needs to be routing to a prefered or sponsored radio station broadcast  */}
                    <a href="https://www.nba.com/rockets/broadcast-information" target="blank"><i class="fa fa-headphones"></i></a>
                </div>
            </div>
        );
    } else {
        return (
            <div className="listen-watch">
                <div className="listen-watch-tab">
                    <a href="https://www.nba.com/watch/video/game-recap-kings-121-rockets-118-8we00b" target="blank">Recap</a>
                </div>
            </div>
        );
    }
}


export default ListenWatchTab;
