import React, {useEffect, useState} from 'react';

import GetPointLeaders from '../../reducers/LeadersAPI';
import GetLastPlayData from '../../reducers/LastPlayAPI';

import PointLeaders from './pointLeaders.jsx';

const LastPlayDetail = (data) => {
    const instance = data.props;

    const [ lastPlayData, setLastPlayData ] = useState(null);
    const [ pointLeaderData, setPointLeaderData ] = useState(null);

    const Canvas = () => {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext("2d");

        const xCoordinate = instance.locX / 2;
        const yCoordinate = instance.locY / 2;

        ctx.beginPath();

        ctx.fillStyle = "black";

        // Set canvas [0,0] to center
        ctx.translate(canvas.width/2,canvas.height/2);

        // Draw 
        ctx.arc(xCoordinate,yCoordinate,5,0,Math.PI*2);
        ctx.closePath();
        ctx.fill();

        ctx.save();
    }

    useEffect(()=>{
        const lastPlay = GetLastPlayData(instance);
        console.log(lastPlay);
        const leaders = GetPointLeaders(instance);
        setLastPlayData(lastPlay);
        console.log(lastPlayData);
        setPointLeaderData(leaders);
        console.log(pointLeaderData);
        const canvas = Canvas();
        return canvas;
    }, [])

    return (
        <div className="last-play">
            <div className="last-play-header">
                <p><img src={lastPlayData.playerImg} alt="player" /> Last Play @ { lastPlayData.clock } { lastPlayData.description }</p>
            </div>

            <div className="last-play-canvas">
                <canvas id="canvas" className="canvas"></canvas>
            </div>
            <LastPlayDetail props={pointLeaderData} />
        </div>
    )
}

export default LastPlayDetail;


