import React, {useState} from 'react';


export default function GetGameData(data) {
    const getStatus = (data) => {

        if(data.st === 2) {
            const status = data.cl;
            return status;
        }
        if(data.st === 1) {
            const status = 'Pregame';
            return status;
        }
        if(data.st === 3) {
            const status = 'Final';
            return status;
        }
        return status;
    };

    getStatus(data); 

    const GameData = {
        'date': data.gdte,
        'time': data.utctm,
        'city': data.ac,
        'state': data.as,
        'home': data.hls.tn,
        'away': data.vls.tn,
        'status': status
    };

    return GameData;
}
