import React, {useState} from 'react';

export default function GetGameData(data) {
    const instance = data;
    console.log(instance);
    console.log('Get Game Data');
    const [opponent, setOpponent] = useState("");
    if(instance.vls.ta === 'HOU') {
        setOpponent(instance.vls.tn);
    }
    else{
        setOpponent(instance.hls.tn);
    }

    const GameData = {
        'date': instance.gdte,
        'time': instance.utctm,
        'city': instance.ac,
        'state': instance.as,
        'opponent': opponent,
        'status': instance.st
    };
    return GameData;
}
