import React, { useEffect, useState } from 'react';


const PointLeaders = (data) => {
    const instance = data.props;

    const [ first, setFirst ] = useState(null);
    const [ second, setSecond ] = useState(null);
    const [ third, setThird ] = useState(null);

    const home = instance.vls.pstsg;
    const away = instance.hls.pstsg;

    const getStars = () => {
        for(var x=0; x<home.length; x++) {
            console.log(home[x]);
            if(home[x].pts === first.pts) {
                if(home[x].ast > first.ast){
                    setThird(second);
                    setSecond(first);
                    setFirst(home[x]);
                } else {
                    setThird(second);
                    setSecond(home[x]);
                }

            } else if(second.pts < home[x].pts ) {
                setThird(second);
                setSecond(home[x]);
            } else if(third.pts < home[x].pts && home[x].pts) {
                setThird(home[x]);
            }
        }
    }


    useEffect(()=>{
        setFirst(home[0]);
        setSecond(home[0]);
        setThird(home[0]);

        getStars();
    }, [])

    return (
        <div className="point-leaders">
            <h1>{first.fn[0]}.{first.ln}</h1>
            <p>{first.pts}</p>
            <h2>{second.fn[0]}.{second.ln}</h2>
            <p>{second.pts}</p>
            <h3>{third.fn[0]}.{third.ln}</h3>
            <p>{third.pts}</p>
        </div>
    );
}

export default PointLeaders;
