import './App.css';
import React, { useState, useEffect} from 'react';
import LoadingScreen from './components/resources/LoadingScreen.jsx';

// import GameDetail from './components/mainCards/GameDetail.jsx'; 
// import GameStats from './components/mainCards/GameStats.jsx';
// import PlayerStatsGameDetail from './components/mainCards/PlayerStatsGameDetail.jsx';
// import TeamStatsDetail from './components/mainCards/TeamStatsDetail.jsx';
import Header from './components/resources/Header.jsx';


function App() {
  const [ error, setError ] = useState(null);
  const [ data, setData ] = useState([]);
  const [ gameData, setGameData ] = useState([]);
  const [ loaded, setLoaded ] = useState(false);

  const GetGameDetail = (instance) => {

    const [ opponent, setOpponent ] = useState("");
    const [ dataCached, setDataCached ] = useState(false);
    if(instance.vls.ta === 'HOU') {
      setOpponent(instance.vls.ta);
      setDataCached(true);
    }
    else{
      setOpponent(instance.hls.ta);
    }

    const GameData = {
      'date': instance.gdte,
      'time': instance.utctm,
      'city': instance.ac,
      'state': instance.as,
      'opponent': opponent,
      'status': instance.st
    };
    return gameData;
  }

  useEffect(() => {
    fetch("https://htxrockets.com/api/archived-game/0022101142")
    .then( (res) => res.json() )
    .then( (res) => {
        setData(res.g);
        console.log(data);
        setGameData(GetGameDetail(data));
        setLoaded(true);
      },
      // Note: handling errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => { 
        setError(error);
        console.log(error);
        setLoaded(true);
      }
    );
  }, [])

  if (error) {
    return <div className="App">Error: {error.message}</div>
  } else if (!loaded) {
    return <div className="loading-page">
        <LoadingScreen />
      </div>
  } else {
    return (
      <div className="App">

        {/* Banner Section */}
        <section className="header">
          <Header props={gameData} />
        </section>


        {/* Last Play */}
        {/* <section className="last-play">
          <p>Last Play: </p>
          <p>{data.cl}</p>
          <p>{data.lpla.de}</p>
        </section> */}

        {/* <div className="table">
          <table>
            <thead>
            </thead>
            <tbody></tbody>
          </table>
        </div> */}
      </div>
    );
  }
}

export default App;
