import React, {useEffect, useState} from 'react';
import headerImg from '../../media/images/headerImg.jpeg'
import GetGameData from '../../reducers/GameDataAPI';

const Header = (data) => {
    const [ instance, setInstance ] = useState(null);
    const [ loaded, setLoaded ] = useState(false);
    useEffect(()=>{
        setInstance(GetGameData(data.props));
        setLoaded(true);
        console.log('game data loaded: ');
        console.log(loaded);
        console.log(instance);
    })

    return (
        <>
            {loaded === true && (
                <div className="header-top">
                    <img src={headerImg} alt="Houston Rockets" />
                    <div className="game-detail">

                        <div className="game-info">
                            <h4>{instance.away} @ {instance.home}</h4>
                            <h5>{instance.time} {instance.date}</h5>
                        </div>

                        <div className="header-resources">
                            <a className="one" target="blank" href="https://watch.nba.com/streaming-subscriptions?cid=nba%3Aleaguepass%3Ateam%3Adig%3Asite%3Awatch_upsell%3Aeg%3Atebou%3Asub%3At-gpm%3ARocketsGameTracker%3Au-glo%3Au-en%3A&_branch_match_id=817613803416432155&_branch_referrer=H4sIAAAAAAAAA8soKSkottLXTywo0MvJzMvWy0tK1EvOz9Uvyk%2FOTi0ptioqt0pPzE0tKUoE8osAE0C3Jy8AAAA%3D">
                                <i className="fas fa-tv"></i> Watch Live
                            </a>
                            <a className="two" target="blank" href="https://sportstalk790.iheart.com/">
                                <i className="fas fa-microphone"></i> Listen
                            </a>
                        </div>
                    </div>
                </div>
            )}
            {loaded === false && (
                <h1>Game data loading...</h1>
            )}
        </>

    )
}

export default Header;
