import React, {useState, useEffect} from 'react';
import GetScoreData from '../../reducers/ScoreAPI';

const ScoreBoard = (data) => {
    const instance = data.props;
    const [scoreDetail, setScoreDetail] = useState(null);
    const [loaded, setLoaded] = useState(false);

    useEffect(()=>{
        setScoreDetail(GetScoreData(instance));
        setLoaded(true);
    }, [])

    return (
        <>
            {loaded === true && (
                <div className="score-board">
                    {/* Home Score */}
                    <div className="home-score">
                        <h5>{scoreDetail.homeTeam}</h5>
                        <h4 className="sb-score">{scoreDetail.homeScore}</h4>
                    </div>

                    {/* Score Board */}
                    <div className="clock">
                        <h5>{scoreDetail.time}</h5>
                        <h4>QTR {scoreDetail.period}</h4>
                    </div>

                    {/* Away Score */}
                    <div className="away-score">
                        <h5>{scoreDetail.awayTeam}</h5>
                        <h4 className="sb-score">{scoreDetail.awayScore}</h4>
                    </div>
                </div>
            )} 
            { loaded === false && (
                <h1>Waiting for score to load...</h1>
            )}
        </>
    )
}

export default ScoreBoard;
